import React, { useState } from "react";
import { useForm } from "react-hook-form";


function Submit(props) {
    const { register, handleSubmit } = useForm()
    // const [nameofFile, setNameofFile] = useState("");


    const onSubmit = (data) => {
        console.log(data)
        const file = data.userFile[0]
        console.log(file)

        const reader = new FileReader();
        reader.onload = function () {

            const readFile = reader.result;
            const parsedFile = JSON.parse(readFile);

            const nodes = [];
            parsedFile.forEach(function (obj) { console.log(obj); });

        }
        reader.readAsText(file);

        //console.log(json);
    }

    // function handleClick() {
    //     setNameofFile(file.name)
    // }


    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <input {...register("userFile")} type="file" />
            <button >Submit</button>
            {/* <h1>{nameofFile}</h1> */}
        </form>
    )

}

export default Submit;
