import React from "react";
import CytoscapeComponent from 'react-cytoscapejs';
import Cytoscape from 'react-cytoscapejs'; 
import edgehandles from "cytoscape-edgehandles";

function CytoNode(props) {

    //declare cytoscape, start empty, add nodes once data is received
    let cy =  new Cytoscape({
        container: document.getElementById('cy'),
        elements: [],
        style: [ 
            {
              selector: 'node',
              style: {
                'background-color': '#69e',
                'label': 'data(id)',
              }
            },

            {
              selector: 'edge',
              style: {
                'width': 1,
                'line-color': '#369',
                'target-arrow-color': '#369',
                'target-arrow-shape': 'triangle',
                'label': 'data(label)',
                'font-size': '14px',
                'color': '#777'
              }
            }
          ],
    });

    //takes in data, then updates cy

    console.log(props.edges);

    const nodeData = [];
    const edgeData = [];

    props.nodes.forEach(function (n) {
        nodeData.push({
          "name": n[0],
          "xpos": n[1].layout.pos[0],
          "ypos": n[1].layout.pos[0],
          "text": n[1].layout.text
        })
    })





    return(
        <div id="cy"></div>
    )
}

export default CytoNode;
